propriedade.valor:
var valor = getParameter('#drpInicioTela#');

if (valor == 'Delimitador'){
  show('txtDelimitador');
  show('grdDelimitador_');
  show('recDelimitador');
  show('txfDelimitador');
  hide('grdPosicao_');
  hide('recPosicao');  

}else if(valor == 'Posição no arquivo'){
  hide('grdDelimitador_');
  hide('txtDelimitador');
  hide('recDelimitador');
  hide('txfDelimitador');
  show('grdPosicao_');
  show('recPosicao');

}else{
  hide('txtDelimitador');
  hide('grdDelimitador_');
  hide('recDelimitador');
  hide('grdPosicao_');
  hide('recPosicao');
}

var v_delimitador = getParameter('$grdLayout.descr_delimitador$');
var v_existe_del = getParameter('$grdDelimitador_.rowid_reg$');
var v_existe_pos = getParameter('$grdPosicao_.rowid_reg$');

proceed();

if(v_delimitador != null){

  setValue('#drpDelimitador#', v_delimitador ); 

  if (v_existe_del != null || v_existe_pos != null){
    setReadOnly('drpDelimitador', true);
  }else{
    setReadOnly('drpDelimitador', false);
  }
}

var valor = getParameter('#drpDelimitador#');

if (valor == 'Delimitador'){
  show('txtDelimitador');
  show('grdDelimitador_');
  show('recDelimitador');
  show('txfDelimitador');
  hide('grdPosicao_');
  hide('recPosicao');
  setValue('drpColunaPos', null);
  setValue('txtInicioPos', null);
  setValue('txtFimPos', null);
  setValue('txtValorPos', null);
  setValue('txtFormatoPos', null);
  setValue('hidNumeroPosEdit', null);

}else if(valor == 'Posição no arquivo'){
  hide('grdDelimitador_');
  hide('txtDelimitador');
  hide('recDelimitador');
  hide('txfDelimitador');
  show('grdPosicao_');
  show('recPosicao');
  setValue('hidNumeroEdit', null);
  setValue('drpColuna', null);
  setValue('txtOrdem', null);
  setValue('txtValor', null);
  setValue('txtFormato', null);
  setValue('txtDelimitador', null);

}

var v_layout = getParameter('$grdLayout.numero$');

if (v_layout == '' || v_layout === null){
  Techne.Hades.alerta('É necessário selecionar o layout do arquivo para incluir dados do arquivo.');
}else{
  proceed();
}

var v_layout = getParameter('$grdLayout.numero$');

if (v_layout == '' || v_layout === null){
  Techne.Hades.alerta('É necessário selecionar o layout do arquivo para incluir dados do arquivo.');
}else{
  proceed();
}
