propriedade.valor:
var x = getParameter('#ddsFlexCampo08#');

if (x == 'NÃO') {
  setReadOnly('srcBanco', false);
  setReadOnly('srcAgencia', false);
  setReadOnly('txtConta', false);
  setReadOnly('drpTipopag', false);
}else{
  setValue('srcBanco',null);
  setValue('srcAgencia','0000');
  setValue('txtConta',null);
  setValue('drpTipopag','SEMCC');



  setReadOnly('srcBanco', true);
  setReadOnly('srcAgencia', true);
  setReadOnly('txtConta', true);
  setReadOnly('drpTipopag', true);
}
