propriedade.valor:
var dup = getParameter('#drpNomeDuplic#');
var msg = 'Já existe um pensionista cadastrado com o nome informado. Deseja continuar?';

if (dup != null) {
 if (dup != '0') {
    var funcOK = function() {
       proceed();
     };
    var funcNOT = function() {};
    var buttons = {
      ok: {text: 'Sim', callback: funcOK},
      cancel: {text: 'Não', callback: funcNOT}
     };
    prompt(msg, buttons);
  } else {
    proceed();
  }
}

var rp = getParameter('#drpOPRepr#');

if (rp == 'N') {
  hide('hypRepres');
  show('txtPossuiRepres');
  show('chkRepresLegal');
  show('txtNomeRepresLegal');
  show('srcRepresLegal');
} else if (rp == 'S') {
  show('hypRepres');
  hide('txtPossuiRepres');
  hide('txtNomeRepresLegal');
  hide('chkRepresLegal');
  hide('srcRepresLegal');
}

 //busca a opção genérica para cidade
var opcaoCidade = getParameter('#drpControleCidade#');
// caso a opção seja diferente de 'S' e 'N' força como opção default 'N'
if ((opcaoCidade != 'S') && (opcaoCidade != 'N')){
opcaoCidade = 'N';
}
 //Chama o controller que mostra/esconde campos do panel de dados pessoais
Techne.Hades.controlaCidade(opcaoCidade ,'txtCidades','txfCidade');
Techne.Hades.controlaCidade(opcaoCidade ,'srcCidade','srcCidadeCod');

// chamada do controller que mostra/esconde campos panel  certidão de nascimento/casamento
Techne.Hades.controlaCidade(opcaoCidade ,'txfCidadeCartorioExp','txfCidadeCartorioCodDesc');
Techne.Hades.controlaCidade(opcaoCidade ,'srcCidadeCartorioExp','srcCidadeCartCod');

// chamada do controller que mostra/esconde campos panel  certidão de óbito/desaparecimento
Techne.Hades.controlaCidade(opcaoCidade ,'txtCidadeCartExpCertPens','txfCidadeCartFimCodDesc');
Techne.Hades.controlaCidade(opcaoCidade ,'srcCidadeCartExpCertPens','srcCidadeCartFimCodDesc')
