propriedade.valor:
var ini = getParameter('#txtDtini#'); 
var fim = getParameter('#txtDtfim#'); 
var msgVerd = 'Data de término deve ser posterior ao início.'; 
var msgErr; 

if (!Techne.Hades.comparaDatas(ini, fim, '>', msgVerd, msgErr)) {
     getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo06'); 
     getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo07'); 
     getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo08'); 
     proceed();
}

var valor = getParameter('#blk001.drpVinculo#');
var atributo_old = getParameter('$grdRegras.numfunc$');

if (atributo_old == null || atributo_old == '') {
    alert('É necessário selecionar um atributo em Regras de pensão previdenciária');
} else {
    if (valor === null || valor == -1 || valor == '') {
        alert('É necessário informar o vínculo do funcionário para incluir um novo registro');
    } else {
        setValue('txtTipoDML', 'U');
        setValue('txtAtributoOld', atributo_old);
        setValue('txtEdicao', 1);
        /*Edição Emerson*/
        setValue('txtOper', 'U');

        getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo06');
        getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo07');
        getControl('recPensao').setDateFormat('d/m/Y', 'txbFlexCampo08');

        proceed();
    }
}

var valor = getParameter('#blk001.drpVinculo#');
if (valor === null || valor == -1 || valor == '') {
    alert('É necessário informar o vínculo do funcionário para incluir um novo registro');
} else {
    setValue('txtTipoDML', 'I');
    proceed();
}

var ini = getParameter('#txtDtini#'); 
var fim = getParameter('#txtDtfim#'); 
var msgVerd = 'Data de término deve ser posterior ao início.'; 
var msgErr; 
Techne.Hades.comparaDatas(ini, fim, '>', msgVerd, msgErr);
