propriedade.valor:
var valor = getParameter('$grdDataDePagamento.id_dia_pagamento$');
var descricao = getParameter('$grdDataDePagamento.descricao$');

if (valor === null || valor == -1 || valor == '') {
    Techne.Hades.alerta('É necessário selecionar um atributo em Data de Pagamento.');
} else {
    proceed();
}

setValue('txbDescricao', descricao);

var valor = getParameter('$grdEmpresa.empresa$');
var nome  = getParameter('$grdEmpresa.nome$');

if (valor === null || valor == -1 || valor == '') {
    Techne.Hades.alerta('É necessário cadastrar uma empresa.');
} else {
    proceed();
}
setValue('txbDescricao', valor+' - '+nome);
