var dup = getParameter('#drpNomeDuplic#');
var msg = 'Já existe um pensionista cadastrado com o nome informado. Deseja continuar?';

if (dup != null) {
 if (dup != '0') {
    var funcOK = function() {
       proceed();
     };
    var funcNOT = function() {};
    var buttons = {
      ok: {text: 'Sim', callback: funcOK},
      cancel: {text: 'Não', callback: funcNOT}
     };
    prompt(msg, buttons);
  } else {
    proceed();
  }
}

var funcionario = getParameter('#blk001.srcMatricula#');
var nome_funcionario = getParameter('$blk001.srcMatricula.descricao$');
if (Techne.Hades.validaFiltrosObrigatorios( [funcionario, nome_funcionario],['o número funcional', 'o número funcional']))
 {
  proceed();
 }

var ini  = getParameter('#dtbNasc#');
var fim1 = getParameter('#txtDtRecadast#');
var fim2 = getParameter('#dtbDtExpedicao#');
var fim3 = getParameter('#dtbDtExp#');


var msg1 = 'Data nascimento deve ser anterior à Dt. Recadastramento.';
var msg2 = 'Data nascimento deve ser anterior à Data de expedição do documento de identificação';
var msg3 = 'Data nascimento deve ser anterior à Data de expedição da certidão de óbito/desaparecimento'

var msgErr;

if (Techne.Hades.comparaDatas(fim1, ini, '<', msg1, msgErr)){ setValue('dtbNasc', null); 
}

if (Techne.Hades.comparaDatas(fim2, ini, '<', msg2, msgErr)){ setValue('dtbNasc', null); 
}

if (Techne.Hades.comparaDatas(fim3, ini, '<', msg3, msgErr)){ setValue('dtbNasc', null); 
}

