var ini = getParameter('#dtbIni#'); 
var fim = getParameter('#dtbFim#'); 
var msgVerd = 'Data de término deve ser posterior ao início.'; 
var msgErr;
if(Techne.Hades.comparaDatas(ini, fim, '>', msgVerd, msgErr)){
setValue('dtbFim', null);
}

var valor = getParameter('#blk001.drpVinculo#');
if (valor === null || valor == -1 || valor == ''){
Techne.Hades.alerta('É necessário informar o vínculo do funcionário para incluir um novo registro');
}
else{
proceed();
}

