propriedade.valor:
var v_tipo_evento = getParameter('$grd2.fimapos_tipoevento$');
var v_cargo = getParameter('$grd2.fimapos_cargo$');
var v_especie = getParameter('$grd2.fimapos_formaprov$');
var v_setor = getParameter('$grd2.fimapos_setor$');
var v_jornada = getParameter('$grd2.fimapos_jornada$');
var v_vaga = getParameter('$grd2.fimapos_numero_vaga$');

setValue('drpTipoevento', v_tipo_evento);
setValue('srcCargo', v_cargo);
setValue('drpFormaProv', v_especie);
setValue('srcSetor', v_setor);
setValue('drpJornada', v_jornada);
setValue('txtVaga', v_vaga);

invalidate('srcCargo');
invalidate('srcSetor');

var ini = getParameter('#txtIniA#');
var fim = getParameter('#txtFimA#');
var msgVerd = 'Data de término deve ser posterior ao início.';
var msgErr;

if(Techne.Hades.comparaDatas(ini, fim, '>', msgVerd, msgErr)){
setValue('txtFimA', null);
}
